import React, { useContext, useState } from 'react';
import { ThemeContext } from '../../ThemeContext';
import './loginPopup.css';

const LoginPopup = ({ show, onClose, onOpenRegister }) => {
  const { isDarkMode, selectedLanguage } = useContext(ThemeContext);
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');

  const translations = {
    english: {
      login: 'Login',
      confirm: 'Confirm',
      register: 'Register',
      username: 'Username',
      password: 'Password'
    },
    polish: {
      login: 'Zaloguj się',
      confirm: 'Potwierdź',
      register: 'Zarejestruj się',
      username: 'Nazwa użytkownika',
      password: 'Hasło'
    }
  };


  const t = translations[selectedLanguage];

  function getCookie(name) {
    const cookieValue = document.cookie.match('(^|;)\\s*' + name + '\\s*=\\s*([^;]+)');
    console.log('cookieValue:', cookieValue);
    return cookieValue ? cookieValue.pop() : '';
  }

  const handleSubmit = (event) => {
    event.preventDefault();

    fetch('http://localhost:8000/accounts/login/', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
        'X-CSRFToken': getCookie('csrftoken'), // Replace with your CSRF token if not using Django template
      },
      body: JSON.stringify({ username, password }),
    })
    .then(response => {
      if (response.ok) {
        // Redirect to dashboard or another page on successful login
        window.location.href = '/dashboard/';
      } else {
        // Display error message if login failed
        setError('Invalid username or password.');
      }
    })
    .catch(error => {
      console.error('Error:', error);
    });
  };

  if (!show) {
    return null;
  }

  return (
    <div className={`popup-overlay ${isDarkMode ? 'dark-mode' : ''}`}>
      <div className={`popup-content ${isDarkMode ? 'dark-mode' : ''}`}>
        <button className="close-icon" onClick={onClose}>X</button>
        <h2>{t.login}</h2>
        <form className="login-form" onSubmit={handleSubmit}>
          <div className="form-group">
            <label htmlFor="username">{t.username}</label>
            <input type="text" id="username" name="username" value={username} onChange={(e) => setUsername(e.target.value)} required />
          </div>
          <div className="form-group">
            <label htmlFor="password">{t.password}</label>
            <input type="password" id="password" name="password" value={password} onChange={(e) => setPassword(e.target.value)} required />
          </div>
          {error && <div className="error-message">{error}</div>}
          <div className="form-actions">
            <button type="button" className="register-button" onClick={onOpenRegister}>{t.register}</button>
            <button type="submit" className="confirm-button">{t.confirm}</button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default LoginPopup;
