import React, { useState } from 'react';
import './home.css';
import Dashboard from '../dashboard/Dashboard';

const Home = () => {
  const [isGuest, setIsGuest] = useState(false);
  const [language, setLanguage] = useState('english'); // Default language is English

  const handleGuestClick = () => {
    setIsGuest(true);
  };

  const handleReturnHome = () => {
    setIsGuest(false);
  };

  const handleLanguageChange = (event) => {
    setLanguage(event.target.value);
  };

  const translateText = (text) => {
    // Translation logic based on selected language
    if (language === 'polish') {
      switch (text) {
        case 'Login':
          return 'Zaloguj siÄ™';
        case 'Register':
          return 'Zarejestruj siÄ™';
        case 'Guest':
          return 'GoÅ›Ä‡';
        default:
          return text;
      }
    } else {
      return text; // Default to English if language is not Polish
    }
  };

  if (isGuest) {
    return <Dashboard onReturnHome={handleReturnHome} translateText={translateText} />;
  }

  return (
    <div className="home-container">
      <div className="language-switch">
        <select value={language} onChange={handleLanguageChange}>
          <option value="english">ðŸ‡ºðŸ‡¸ English</option>
          <option value="polish">ðŸ‡µðŸ‡± Polish</option>
        </select>
      </div>
      <div className="theme-switch">
        {/* Add the switch component for light/dark mode */}
      </div>
      <button className="home-button" onClick={() => {}}> {translateText('Login')} </button>
      <button className="home-button" onClick={() => {}}> {translateText('Register')} </button>
      <button className="home-button" onClick={handleGuestClick}> {translateText('Guest')} </button>
    </div>
  );
};

export default Home;
