import React, { useContext, useState, useEffect } from 'react';
import { ThemeContext } from '../../ThemeContext';
import './registerPopup.css';

const RegisterPopup = ({ show, onClose, onOpenLogin, onLogin }) => {
  const { isDarkMode, selectedLanguage } = useContext(ThemeContext);
  const [username, setUsername] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [confirmPassword, setConfirmPassword] = useState('');
  const [csrfToken, setCsrfToken] = useState('');

  useEffect(() => {
    fetch('/csrf_token/')
      .then(response => response.json())
      .then(data => {
        saveToFile(data.csrfToken, 'csrf_token.html', 'text/html');
        setCsrfToken(data.csrfToken);
      })
      .catch(error => console.error('Error fetching CSRF token:', error));
  }, []);

  if (!show) {
    return null;
  }

  const saveToFile = (content, filename, contentType) => {
    const blob = new Blob([content], { type: contentType });
    const url = URL.createObjectURL(blob);
    const link = document.createElement('a');
    link.href = url;
    link.setAttribute('download', filename);
    document.body.appendChild(link);
    link.click();
    document.body.removeChild(link);
    URL.revokeObjectURL(url);
  };

  const translations = {
    english: {
      login: 'Login',
      confirm: 'Confirm',
      register: 'Register',
      username: 'Username',
      email: 'Email',
      password: 'Password',
      confirmPassword: 'Confirm Password'
    },
    polish: {
      login: 'Zaloguj się',
      confirm: 'Potwierdź',
      register: 'Zarejestruj się',
      username: 'Nazwa użytkownika',
      email: 'Email',
      password: 'Hasło',
      confirmPassword: 'Potwierdź hasło'
    }
  };

  const t = translations[selectedLanguage];

  const handleRegister = async (e) => {
    e.preventDefault();
    if (password !== confirmPassword) {
        console.error('Passwords do not match');
        return;
    }
    const response = await fetch('/accounts/register/', {
        method: 'POST',
        headers: {
            'Content-Type': 'application/json',
            'X-CSRFToken': csrfToken
        },
        body: JSON.stringify({ username, email, password, confirmPassword })
    });
    if (response.ok) {
        // Handle successful registration
        window.location.href = '/dashboard'; // Redirect to dashboard
    } else {
        // Handle errors
        console.error('Registration failed');
    }
    saveToFile(data.csrfToken, 'csrf_token.html', 'text/html');
};

// function getCookie(name) {
//   const cookieValue = document.cookie
//       .split('; ')
//       .find(row => row.startsWith(name + '='))
//       .split('=')[1];
//   return cookieValue;
// }

function getCookie(name) {
  let cookieValue = null;
  if (document.cookie && document.cookie !== '') {
      const cookies = document.cookie.split(';');
      for (let i = 0; i < cookies.length; i++) {
          const cookie = cookies[i].trim();
          if (cookie.substring(0, name.length + 1) === (name + '=')) {
              cookieValue = decodeURIComponent(cookie.substring(name.length + 1));
              break;
          }
      }
  }
  return cookieValue;
}

const csrftoken = getCookie('csrftoken');

  return (
    <div className={`popup-overlay ${isDarkMode ? 'dark-mode' : ''}`}>
      <div className={`popup-content ${isDarkMode ? 'dark-mode' : ''}`}>
        <button className="close-icon" onClick={onClose}>X</button>
        <h2>{t.register}</h2>
        <form className="login-form" onSubmit={(e) => { console.log('Form submitted'); handleRegister(e); }}>
            <div className="form-group">
              <label htmlFor="email">{t.email}</label>
              <input type="email" id="email" name="email" value={email} onChange={(e) => setEmail(e.target.value)} required />
            </div>
          <div className="form-group">
            <label htmlFor="username">{t.username}</label>
            <input type="text" id="username" name="username" value={username} onChange={(e) => setUsername(e.target.value)} required />
          </div>
          <div className="form-group">
            <label htmlFor="password">{t.password}</label>
            <input type="password" id="password" name="password" value={password} onChange={(e) => setPassword(e.target.value)} required />
          </div>
            <div className="form-group">
              <label htmlFor="confirmPassword">{t.confirmPassword}</label>
              <input type="password" id="confirmPassword" name="confirmPassword" value={confirmPassword} onChange={(e) => setConfirmPassword(e.target.value)} required />
            </div>
          <div className="form-actions">
            <button type="submit">Potwierdź</button>
            <button type="button" className="toggle-mode-button" onClick={onOpenLogin}>{t.login}</button>
            <button type="submit" className="confirm-button">{t.confirm}</button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default RegisterPopup;
