import React, { useState } from 'react';
import './home.css';
import Dashboard from '../dashboard/Dashboard';

const Home = () => {
  const [isGuest, setIsGuest] = useState(false);
  const [isDarkMode, setIsDarkMode] = useState(false);
  const [language, setLanguage] = useState('english');

  const handleGuestClick = () => {
    setIsGuest(true);
  };

  const handleReturnHome = () => {
    setIsGuest(false);
  };

  const handleLanguageChange = (e) => {
    setLanguage(e.target.value);
  };

  const handleModeChange = () => {
    setIsDarkMode(!isDarkMode);
  };

  if (isGuest) {
    return <Dashboard onReturnHome={handleReturnHome} />;
  }

  return (
    <div className={`home-container ${isDarkMode ? 'dark-mode' : ''}`}>
      <div className="top-bar">
        <label className="switch">
          <input type="checkbox" checked={isDarkMode} onChange={handleModeChange} />
          <span className="slider round"></span>
        </label>
        <select className="language-dropdown" value={language} onChange={handleLanguageChange}>
          <option value="english">ðŸ‡ºðŸ‡¸ English</option>
          <option value="polish">ðŸ‡µðŸ‡± Polish</option>
        </select>
      </div>
      <div className="home-buttons">
        <button className="home-button">{language === 'polish' ? 'Zaloguj siÄ™' : 'Login'}</button>
        <button className="home-button">{language === 'polish' ? 'Zarejestruj siÄ™' : 'Register'}</button>
        <button className="home-button" onClick={handleGuestClick}>{language === 'polish' ? 'GoÅ›Ä‡' : 'Guest'}</button>
      </div>
    </div>
  );
};

export default Home;
